$(document).ready(function(){$(".regular_testimonials .wrap_testimonials .section_title h2").live("click",function(){var e=$(this).parent().parent(".wrap_testimonials");if($(e).hasClass("open_testimonials"))$(e).animate({height:35},1e3,function(){$(e).removeClass("open_testimonials"),$(e).find("h2 span").removeClass("open"),$(e).find("h2 span").addClass("closed")});else{var s=$(e).height()+30+$(e).find(".testimonial_open").height();$(e).animate({height:s},1e3,function(){$(e).addClass("open_testimonials"),$(e).find("h2 span").removeClass("closed"),$(e).find("h2 span").addClass("open")})}})});